mapper.select:
select u.user_name,u.user_color,a.apply_day,a.apply_section,a.apply_week,c.course_name,c.course_class
from user u,lab_room l,apply a,course c
WHERE u.user_id=a.user_id
  and l.room_id=a.room_id
  and c.course_id = a.course_id
  and a.apply_week = #{apply_week}
  and a.room_id = #{room_id}
  and a.years = #{years}
  and a.iterm = #{iterm}

select u.user_name,u.user_color,a.apply_day,a.apply_section,a.apply_week,c.course_name,c.course_class
from user u,lab_room l,apply a,course c
WHERE u.user_id=a.user_id
  and l.room_id=a.room_id
  and c.course_id = a.course_id
  and a.room_id = #{room_id}
  and a.years = #{years}
  and a.iterm = #{iterm}

select a.apply_id, a.apply_week, a.apply_day, a.apply_section, a.user_id, c.course_name, c.course_class, u.user_name, l.room_name, l.room_code from apply a left join course c on a.course_id = c.course_id left join user u on a.user_id = u.user_id left join lab_room l on a.room_id = l.room_id
and a.user_id = #{ userName}
and a.apply_week = #{applyWeek}
and a.room_id = #{roomCode}
and a.years = #{years}
and a.iterm = #{iterm}
ORDER BY a.apply_day,a.apply_section ASC limit #{start},#{rows};

select count(1) from apply a left join course c on a.course_id = c.course_id left join user u on a.user_id = u.user_id left join lab_room l on a.room_id = l.room_id
and a.user_id = #{userName}
and a.apply_week = #{applyWeek}

select
count(1)
from apply


        and years = #{years}


        and iterm = #{iterm}


        and room_id = #{room_id}
